import React, { useState } from 'react';
import Dropdown from './components/Dropdown';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Home from './Home';
import About from './About';
import Contact from './Contact';

function App() {
  const [selectedGrouping, setSelectedGrouping] = useState('');
  const [selectedOrdering, setSelectedOrdering] = useState('');

  const handleGroupingSelection = (value) => {
    setSelectedGrouping(value);
  };

  const handleOrderingSelection = (value) => {
    setSelectedOrdering(value);
  };

  return (
    <Router>
      <div className="App">
        <h1>App Component</h1>
        <Dropdown
          onSelectGrouping={handleGroupingSelection}
          onSelectOrdering={handleOrderingSelection}
        />
        <div>Selected Grouping in App: {selectedGrouping}</div>
        <div>Selected Ordering in App: {selectedOrdering}</div>
        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/about" component={About} />
          <Route path="/contact" component={Contact} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
