import React, { useState } from 'react';
import './Dropdown.css'; // Import your CSS file for styling

const Dropdown = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isSubDropdownOpen, setIsSubDropdownOpen] = useState(false);
  const [selectedGrouping, setSelectedGrouping] = useState('');
  const [selectedOrdering, setSelectedOrdering] = useState('');

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const toggleSubDropdown = () => {
    setIsSubDropdownOpen(!isSubDropdownOpen);
  };

  const handleGroupingSelection = (value) => {
    setSelectedGrouping(value);
  };

  const handleOrderingSelection = (value) => {
    setSelectedOrdering(value);
  };

  return (
    <div className="dropdown">
      <button className="dropdown-button" onClick={toggleDropdown}>
        Display
      </button>
      {isDropdownOpen && (
        <div className="dropdown-content">
          <div className="sub-dropdown">
            <a href="#" onClick={toggleSubDropdown}>
              Grouping
            </a>
            {isSubDropdownOpen && (
              <div className="sub-dropdown-content">
                <a href="#" onClick={() => handleGroupingSelection('Status')}>
                  Status
                </a>
                <a href="#" onClick={() => handleGroupingSelection('Priority')}>
                  Priority
                </a>
                <a href="#" onClick={() => handleGroupingSelection('Users')}>
                  Users
                </a>
              </div>
            )}
          </div>
          <div className="sub-dropdown">
            <a href="#" onClick={toggleSubDropdown}>
              Ordering
            </a>
            {isSubDropdownOpen && (
              <div className="sub-dropdown-content">
                <a href="#" onClick={() => handleOrderingSelection('Priority')}>
                  Priority
                </a>
                <a href="#" onClick={() => handleOrderingSelection('Text')}>
                  Text
                </a>
              </div>
            )}
          </div>
          <div>Selected Grouping: {selectedGrouping}</div>
          <div>Selected Ordering: {selectedOrdering}</div>
        </div>
      )}
    </div>
  );
};

export default Dropdown;
