import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from './components/Card';

export default function Priority1() {
  const [data, setData] = useState(null);
  const [card_list_Backlog, setcard_list_Backlog] = useState([]);
  const [card_list_Todo, setcard_list_Todo] = useState([]);
  const [card_list_Inprogress, setcard_list_Inprogress] = useState([]);
  const [card_list_Done, setcard_list_Done] = useState([]);
  const [card_list_Cancelled, setcard_list_Cancelled] = useState([]);
  const [addCardExecuted, setAddCardExecuted] = useState(false); 

  const fetchData = async () => {
    try {
      const response = await axios.get('https://api.quicksell.co/v1/internal/frontend-assignment');
      setData(response.data.tickets);
    } catch (error) {
      console.error('Axios error:', error);
    }
  };

  const add_card = () => {
    const newcardlist_Backlog = [];
    const newcardlist_Todo = [];
    const newcardlist_Inprogress = [];
    const newcardlist_Done = [];
    const newcardlist_Cancelled = [];

    for (const item of data) {
      const card = <Card key={item.id} name={item.id} desc={item.title} tag={item.tag[0]} />;

      if (item.status === 'Backlog') {
        newcardlist_Backlog.push(card);
      } else if (item.status === 'Todo') {
        newcardlist_Todo.push(card);
      } else if (item.status === 'In progress') {
        newcardlist_Medium.push(card);
      } else if (item.status === 'Done') {
        newcardlist_High.push(card);
      } else if (item.status === 'Cancelled') {
        newcardlist_Urgent.push(card);
      }
    }

    setcard_list_Backlog(newcardlist_Backlog);
    setcard_list_Todo(newcardlist_Todo);
    setcard_list_Inprogress(newcardlist_Inprogress);
    setcard_list_Done(newcardlist_Done);
    setcard_list_Cancelled(newcardlist_Cancelled);
    setAddCardExecuted(true); 
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    if (data && !addCardExecuted) { 
      add_card();
    }
  }, [data, addCardExecuted]);


 //console.log(card_list_Nopriority.length)

  return (
    <div style={{ display: 'flex', backgroundColor: 'rgb(211, 225, 230)', height: '100vh',overflowY: "auto" }}>
      <div style={{ width: '20%' }}><div style={{display:"flex"}}>Backlog<div style={{position:"relative",marginLeft:"15px"}}>{card_list_Backlog.length}</div></div>{card_list_Backlog}</div>
      <div style={{ width: '20%' }}><div style={{display:"flex"}}>Todo<div style={{position:"relative",marginLeft:"15px"}}>{card_list_Todo.length}</div></div>{card_list_Todo}</div>
      <div style={{ width: '20%' }}><div style={{display:"flex"}}>In progress<div style={{position:"relative",marginLeft:"15px"}}>{card_list_Inprogress.length}</div></div>{card_list_Inprogress}</div>
      <div style={{ width: '20%' }}><div style={{display:"flex"}}>Done<div style={{position:"relative",marginLeft:"15px"}}>{card_list_Done.length}</div></div>{card_list_Done}</div>
      <div style={{ width: '20%' }}><div style={{display:"flex"}}>Cancelled<div style={{position:"relative",marginLeft:"15px"}}>{card_list_Cancelled.length}</div></div>{card_list_Cancelled}</div>
    </div>
  );
}
