import React, { useState } from 'react';
import Dropdown from './Dropdown'; // Import your Dropdown component

function App() {
  const [selectedGrouping, setSelectedGrouping] = useState('');
  const [selectedOrdering, setSelectedOrdering] = useState('');

  const handleGroupingSelection = (value) => {
    setSelectedGrouping(value);
  };

  const handleOrderingSelection = (value) => {
    setSelectedOrdering(value);
  };

  return (
    <div className="App">
      <h1>App Component</h1>
      <Dropdown
        onSelectGrouping={handleGroupingSelection}
        onSelectOrdering={handleOrderingSelection}
      />
      <div>Selected Grouping in App: {selectedGrouping}</div>
      <div>Selected Ordering in App: {selectedOrdering}</div>
    </div>
  );
}

export default App;
